mapper.select:
select

from t_balance
where deleted = 0

    and user_id = #{userId}


    and status = #{status}


    and username = #{username}


    and phone = #{phone}

order by id desc

mapper.insert:

    SELECT LAST_INSERT_ID()

insert into t_balance (user_id, username, phone,
money, status, type,
create_time, update_time, deleted
)
values (#{userId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR},
#{money,jdbcType=DECIMAL}, #{status,jdbcType=INTEGER}, #{type,jdbcType=INTEGER},
#{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=INTEGER}
)

mapper.update:
update t_balance
set deleted = 1
where id = #{id}
