mapper.select:
select

from t_order
where deleted = 0

    and user_id = #{userId}


    and status = #{status}


    and agent_id = #{agentId}


    and order_no = #{orderNo}


    and user_id in (select id from t_user where deleted = 0 and username like "%"#{username}"%")


    and box_id in (select id from t_box where deleted = 0 and name = #{boxName})


    and create_time > #{startTime}


    and #{endTime} > create_time

order by id desc

mapper.update:
update t_order
set deleted = 1
where id = #{id}
